import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import React from "react";
import HomeScreen from "../tabScreem/HomeScreen";
import SubsidiaryScreen from "../tabScreem/SubsidiaryScreen";
import { Octicons } from "@expo/vector-icons";
import CredentialScreen from "../tabScreem/CredentialScreen";
import MenuScreen from "../tabScreem/MenuScreen";

const Tab = createBottomTabNavigator();

const TabNavigation = () => {
  return (
    <Tab.Navigator
      initialRouteName="Home"
      options={{
        headerStyle: false,
      }}
      screenOptions={{
        tabBarLabelStyle: { color: "#fff" },
        tabBarStyle: { backgroundColor: "#015cb5", height: 20 },
        tabBarActiveBackgroundColor: "#013161",
        tabBarIcon: "#fff",
      }}
    >
      <Tab.Screen
        name="Home"
        component={HomeScreen}
        options={{
          tabBarIcon: () => {
            return <Octicons name="home" size={24} color="#fff" />;
          },
        }}
      />
      <Tab.Screen
        name="Subsidiary"
        component={SubsidiaryScreen}
        options={{
          tabBarIcon: () => {
            return <Octicons name="location" size={24} color="#fff" />;
          },
        }}
      />
      <Tab.Screen
        name="Credential"
        component={CredentialScreen}
        options={{
          tabBarIcon: () => {
            return <Octicons name="id-badge" size={24} color="#fff" />;
          },
        }}
      />
      <Tab.Screen
        name="Menu"
        component={MenuScreen}
        options={{
          tabBarIcon: () => {
            return <Octicons name="apps" size={24} color="#fff" />;
          },
        }}
      />
    </Tab.Navigator>
  );
};

export default TabNavigation;
